import React from 'react'
import Editor from './Editor'
import { useState } from 'react'
import ColumnResizer from "react-column-resizer";

export default function App() {


  const [htmlValue, setHtml] = useState("<h1>Hello World!</h1>");
  const [cssValue, setCSS] = useState(`body{
    color : red;
}`)
  const [jsValue, setJs] = useState(`console.log("Hello World")`);

  const srcCode = <html>{htmlValue}</html>;

  return (
    <center>
      <div className="pane top-pane">
        <table>
          <tbody>
            <tr>
              <Editor
                displayName="HTML"
                lang="xml"
                value={htmlValue}
                setValue={setHtml}

              />

              <ColumnResizer
                className="columnResizer resizer"
                minWidth={0}

              />

              <Editor
                displayName="CSS"
                lang="css"
                value={cssValue}
                setValue={setCSS}

              />


              <ColumnResizer
                className="columnResizer resizer"
                minWidth={0}

              />

              <Editor
                displayName="JS"
                lang="javascript"
                value={jsValue}
                setValue={setJs}

              />

            </tr>
            <tr>

              <iframe height="100%" width="100vw"
                srcDoc={htmlValue + "<style>" + cssValue + "</style>" + "<script>" + jsValue + "<script>"}>

              </iframe>
            </tr>
          </tbody>
        </table>
      </div>





    </center>
  )
}
