import React from "react";
import CodeEditor from "./CodeEditor";
import "bootstrap/dist/css/bootstrap.min.css";
import { initialHTML, initialCSS, initialJS } from "./InitialValues";
import { useState, useEffect } from "react";
import ColumnResizer from "react-column-resizer";
import SmoothRender from "react-smooth-render";

export default function App() {
  const [htmlValue, setHtml] = useState(initialHTML);
  const [cssValue, setCSS] = useState(initialCSS);
  const [jsValue, setJs] = useState(initialJS);
  const [Code, setCode] = useState(`
<html>
<body>${htmlValue}</body>
<style>${cssValue}</style>
<script>${jsValue}</script>
</html>`);
  const [hidden, toggle] = useState(false);

  useEffect(() => {
    const timeout = setTimeout(() => {
      setCode(`
      <html>
      <body>${htmlValue}</body>
      <style>${cssValue}</style>
      <script>${jsValue}</script>
      </html>`);
    }, 250);
    return () => clearTimeout(timeout);
  }, [htmlValue, cssValue, jsValue]);

  return (
    <center>
      <div className="pane top-pane">
        <table>
          <tbody>
            <tr>
              <CodeEditor
                displayName="HTML"
                lang="html"
                value={htmlValue}
                setValue={setHtml}
                hidden={hidden}
                toggle={toggle}
              />

              <ColumnResizer className="columnResizer resizer" minWidth={0} />

              <CodeEditor
                displayName="CSS"
                lang="css"
                value={cssValue}
                setValue={setCSS}
                hidden={hidden}
                toggle={toggle}
              />

              <ColumnResizer className="columnResizer resizer" minWidth={0} />

              <CodeEditor
                displayName="Javascript"
                lang="javascript"
                value={jsValue}
                setValue={setJs}
                hidden={hidden}
                toggle={toggle}
              />
            </tr>
          </tbody>
        </table>
      </div>
      <SmoothRender hidden={hidden} timing={350}>
        <div className="outputScreenf">
          <iframe
            srcDoc={Code}
            width="100%"
            height="100%"
            frameBorder="false"
            sandbox="allow-scripts"
          ></iframe>
        </div>
      </SmoothRender>
    </center>
  );
}
