import React from "react";
import CodeEditor from "./CodeEditor";
import "bootstrap/dist/css/bootstrap.min.css";
import { initialHTML, initialCSS, initialJS } from "./InitialValues";
import { useState, useEffect } from "react";
import ColumnResizer from "react-column-resizer";

export default function App() {
  const [htmlValue, setHtml] = useState(initialHTML);
  const [cssValue, setCSS] = useState(initialCSS);
  const [jsValue, setJs] = useState(initialJS);
  const [code, setCode] = useState(``);

  useEffect(() => {
    const timeout = setTimeout(() => {
      setCode(`
      <html>
      <body>${htmlValue}</body>
      <style>${cssValue}</style>
      <script>${jsValue}</script>
      </html>`);
    }, 250);
    return () => clearTimeout(timeout);
  }, [htmlValue, cssVlue, jsValue]);

  let Code =
    "<html>" +
    htmlValue +
    "</html>" +
    "<style>" +
    cssValue +
    "</style>" +
    "<script>" +
    jsValue +
    "</script>";

  return (
    <center>
      <div className="pane top-pane">
        <table>
          <tbody>
            <tr>
              <CodeEditor
                displayName="HTML"
                lang="html"
                value={htmlValue}
                setValue={setHtml}
              />

              <ColumnResizer className="columnResizer resizer" minWidth={0} />

              <CodeEditor
                displayName="CSS"
                lang="css"
                value={cssValue}
                setValue={setCSS}
              />

              <ColumnResizer className="columnResizer resizer" minWidth={0} />

              <CodeEditor
                displayName="Javascript"
                lang="javascript"
                value={jsValue}
                setValue={setJs}
              />
            </tr>
          </tbody>
        </table>
      </div>
      //
      <div className="outputScreen">
        <iframe
          srcDoc={Code}
          width="100%"
          height="100%"
          frameBorder="false"
        ></iframe>
      </div>
    </center>
  );
}
